import axios from "axios";
import React, { useState } from "react";
import Styled from "styled-components";
function AddArticle(props) {
  const [authername, setAuthername] = useState("");
  const [title, setTitle] = useState("");
  const [article, setArticle] = useState("");
  const [image, setFilename] = useState("");



  // const onChangeFile = () => {
  //   const formData = new FormData();
  //   formData.append("title", title);
  //   formData.append("authername", authername);
  //   formData.append("article", article);
  //   formData.append("image", image);


  //   axios
  //     .post(`http://localhost:5000/add`, formData)
  //     .then((res) => {
  //       alert("File Upload success");
  //     })
  //     .catch((err) => alert("File Upload Error"));
  // };

  // const onChangeFile =e=>{
  //     setFilename(e.target.files)
  // }
  const ChangeOnClick = (e) => {
    e.preventDefault();
    const formData = new FormData();
      formData.append("title", title);
      formData.append("authername", authername);
      formData.append("article", article);
      formData.append("image", image);

    // const articles = {
    //   title,
    //   article,
    //   authername,
    //   image
    // };
    setTitle("");
    setArticle("");
    setAuthername("");
    // setFilename("");
    console.log(formData);
    axios
      .post(`http://localhost:5000/add`,  formData,)
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  };

  return (
    <MainContainer>
      <h1>Add your blog</h1>
      <form onSubmit={ChangeOnClick} encType="multipart/form-data">
        <div className="mb-3">
          <label htmlFor="authername" className="form-label">
            auther
          </label>
          <input
            type="text"
            value={authername}
            onChange={(e) => setAuthername(e.target.value)}
            className="form-control"
            placeholder="authername"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            title
          </label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="form-control"
            placeholder="title"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="article" className="form-label">
            article
          </label>
          <textarea
            value={article}
            onChange={(e) => setArticle(e.target.value)}
            className="form-control"
          >
            write
          </textarea>
        </div>
        <div className="form-group">
          <label htmlFor="file">choose your file</label>
          <input
            type="file"
            filename="image"
            accept=".png, .jpg, .jpeg"
            className="form-control-file"
            onChange={(e) => setFilename(e.target.files[0])}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          add
        </button>
      </form>
    </MainContainer>
  );
}

export default AddArticle;
const MainContainer = Styled.div`
margin:3rem auto;
padding:4rem;
width:31.25rem
`;
