import React from 'react'
import Styled from 'styled-components'
function Articles(posts) {
    return (
        <MainContainer>
            {posts.posts.map((article,key)=>{
                    console.log(article)
                    return(
                        <div className="container">
                    <h2>{article.title}</h2>
                   <p>{article.article}</p>
                   <span >
                       {article.authername}</span>

                    <div className="row my-5">

                   <div className="col-sm-2">
                       <a href="" className="btn btn-outline-success">edit</a>
                   </div>

                   <div className="col-sm-2">
                       <a href="" className="btn btn-outline-danger">delete</a>    

                    </div>

                    </div>

                </div>
                    )
                })


            }
        </MainContainer>

    )
}

export default Articles
const MainContainer = Styled.div `
margin:7rem 0;
`
