    setImage(e.target.files[0])

    axios
    .post(`http://localhost:5000/upload`, da)
    .then((res) => console.log(res.data))
    .catch((err) => console.log(err));


  }

  const onChangeFile = (e) => {

    e.preventDefault();


    // axios
    // .post(`http://localhost:5000/upload`, image)
