    axios({
      method: "post",
      url: "http://localhost:5000/add",
      headers:{
        'Content-Type':'application/x-www-form-urlencoded'
      },
      data: articles,
    })
    .then((res) => console.log(res.data))
    .catch((err) => console.log(err));



  };
