  return (
    <MainContainer>
      <h1>Add your blog</h1>
      <form onSubmit={onChangeFile} encType="multipart/form-data">
        <div className="mb-3">
          <label htmlFor="authername" className="form-label">
            auther
          </label>
          <input
            type="text"
            value={authername}
            onChange={(e) => setAuthername(e.target.value)}
            className="form-control"
            placeholder="authername"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            title
          </label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="form-control"
            placeholder="title"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="article" className="form-label">
            article
          </label>
          <textarea
            value={article}
            onChange={(e) => setArticle(e.target.value)}
            className="form-control"
