        </div>
        <div className="form-group">
          <label htmlFor="file">choose your file</label>
          <input
            type="file"
            filename="picutre"
            className="form-control-file"
            onChange={onFileChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          add
        </button>
      </form>
    </MainContainer>
  );
}

export default AddArticle;
